import React, { useEffect, useRef, useState } from "react";
import { select } from "d3";

const DrawStudio2 = () => {
    const svgRef: any = useRef()
    const [data, setData] = useState([[5, 8, 'table'],[5, 10, 'chair']])

    useEffect(() => {
        const svg = select(svgRef.current)
        svg.
            selectAll("cirle")
            .data(data)
            .join(
                (enter) => enter.append("circle"),
                (update) => update.attr("class", "updated"),
                (exit) => exit.remove()
            )
            .attr("cx", (value) => value)
            .attr("cy", (value) => value)
            .attr("storke", "black")
    }, [data])
}

export default DrawStudio2
