import React, { useEffect, useRef, useState } from "react";

import Header from "./Header";
import Footer from "./Footer";
import Home from "./Home";
import DrawStudio from "./DrawStudio";
import ShowAvailable from "./ShowAvailble";

import "../styles/layout.css"


type Object = {
    x: number;
    y: number;
    type: "chair" | "table" | "sitting";
};

type Info = {
    time: number
    now?: number
    data: Object[]
}

type Props = {
	objects?: Info;
};

const DesktopLayout = ({props} : any) => {
    const [data, setData] = useState<Info>()

    const getData = async () => {
        try {
            const res = await fetch(
                "http://10.18.243.73:3000/data"
            )

            const data = await res.json()
            console.log(data)
            return data
        } catch (e) {
            console.error(e)
        }

        const initData = (data: any) => {
            const info:Info = {
                time: data.time,
                data: data.data.map((object:any) => ({
                    x: object.x,
                    y: object.y,
                    type: object.type
                })),
                now: new Date().getTime()
            }

            const props: Props = {
                objects: info,
            }

            return info
        }
        setData(initData)
    }

    useEffect(() => {
        getData()
        console.log(data)
    }, [])

    return (
        <>
            <Header />
            <div className="container_desktop">
                <div className="left_desktop">
                    <DrawStudio objects={data} />
                    <div className="left_bottom">
                        <p>최근 업데이트 시점: {}</p>
                        <button> 새로고침 </button>
                    </div>
                </div>
                <div className="right_desktop">
                </div>
            </div>
            <Footer />
        </>
    )
}

export default DesktopLayout
