import React, { useEffect, useRef, useState } from "react";

import Header from "./Header";
import Footer from "./Footer";
import DrawStudio from "./DrawStudio";
import ShowAvailable from "./ShowAvailble";

import "../styles/layout.css"

type Object = {
    x: number;
    y: number;
    type: "chair" | "table" | "sitting";
};

type Info = {
    time: number
    now?: number
    data: Object
}

type Props = {
	objects: Info;
};

const MobileLayout = ({props} : any) => {
    const [data, setData] = useState<Info>()

    const getData = async () => {
        try {
            const res = await fetch("http://10.18.243.73:3000/data");
            const fetchData = await res.json();
            const initData = (it: any) => {
                const info: Info = {
                time: it.time,
                data: it.data.map((object: any) => ({
                    x: object.x,
                    y: object.y,
                    type: object.type,
                })),
                now: new Date().getTime(),
                };
                return info;
            };
            setData(initData(fetchData));
        } catch (e) {
            console.error(e);
            }
        };

    useEffect(() => {
        getData()
        console.log(data)
    }, [])


    return (
        <>
            <Header />
            <div className="container_mobile">
                <div className="up_mobile">
                    {data ? <DrawStudio objects={data} /> : <p className="loading">Loading...</p>}
                     <div className="left_bottom">
                        <p>최근 업데이트 시점: {}</p>
                        <button> 새로고침 </button>
                    </div>
                </div>
                <div className="down_mobile">
                </div>
            </div>
            <Footer />
        </>
    )
}

export default MobileLayout
