import React, { useRef, useEffect } from "react";

type Object = {
  x: number;
  y: number;
  type: "chair" | "table";
};

function drawTable(ctx: CanvasRenderingContext2D, x: number, y: number) {
  ctx.fillRect(x, y, 50, 50);
}

function drawChair(ctx: CanvasRenderingContext2D, x: number, y: number) {
  ctx.beginPath();
  ctx.arc(x + 25, y + 25, 25, 0, 2 * Math.PI);
  ctx.fill();
}

type Props = {
  objects: Object[];
};

function Canvas({ objects }: Props) {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) {
      return;
    }

    const ctx = canvas.getContext("2d");
    if (!ctx) {
      return;
    }

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    for (const obj of objects) {
      if (obj.type === "table") {
        drawTable(ctx, obj.x, obj.y);
      } else if (obj.type === "chair") {
        drawChair(ctx, obj.x, obj.y);
      }
    }
  }, [objects]);

  return <canvas ref={canvasRef} width={500} height={500} />;
}

export default Canvas;
