import React, { useEffect, useRef, useState } from "react";
import { select } from "d3";

const DrawStudio2 = () => {
    const svgRef: any = useRef()
    const [data, setData] = useState([{cx : 50, cy: 80, r: 60}, {cx : 100, cy: 150, r: 60}])

    useEffect(() => {
        const svg = select(svgRef.current)
        svg
            .attr("viewBox", '60, 60, 500, 500')
            .selectAll("cirle")
            .data(data)
            .join(
                (enter) => enter.append("circle"),
                (update) => update.attr("class", "updated"),
                (exit) => exit.remove()
            )
            .attr("cx", (value) => value.cx)
            .attr("cy", (value) => value.cy)
            .attr("r", (value) => value.r)
            .attr("fill", "black")
    }, [data])

    const increaseData = () => {
        setData(data.map((value) => value));
        };

    const decreaseData = () => {
        setData(data.map((value) => value));
        };

    return (
        <React.Fragment>
        <svg ref={svgRef}>
            <circle />
        </svg>
        </React.Fragment>
    )
}

export default DrawStudio2
